ruleSearch.bookList:
@js:
var list=[];
var reg=new RegExp(/s1"><a.href="([^"]+)[\s\S]+?>([^<]+)[\s\S]+?s2.+?>([^<]+)[\s\S]+?s3.+?>([^<]+)[\s\S]+?s4.+?>([^<]+)[\s\S]+?s5.+?>[^<]+[\s\S]+?s6.+?>([^<]+)/g);
var tmp,cover;
while((tmp=reg.exec(result))){
//封面链接
cover=(tmp[1].match(/d{1,2}/)) ? (tmp[1].replace(/\/(\d+)\//,'/files/article/image/0/$1/$1s.jpg')):(tmp[1].replace(/\/((\d*)\d{3})\//,'/files/article/image/$2/$1/$1s.jpg').replace(/\/\//,'/0/'));

list.push({q:tmp[2],w:tmp[4],e2:tmp[5]+'字',e1:tmp[6],r:tmp[3],t:cover,y:tmp[1]});
}list;

