ruleContent.content:
@js:
c=result.match(/<div id="content">([\s\S]+)<div class="jump"/)[1];
if(result.match(/<script>document.getElementById\("[^"]+"\).innerHTML = "([^"]+)";<\/script>/)){
a=result.match(/<script>document.getElementById\("shu"\).innerHTML = "([^"]+)";<\/script>/)[1];
b=result.match(/<script>document.getElementById\("hys"\).innerHTML = "([^"]+)";<\/script>/)[1];
result=c+b+a}else{result=c};
result=String(result).replace(/红\s*颜\s*书\s*吧/g,"").replace(/.*本章未完.*/g,"").replace(/.*手机阅读请.*/g,"").replace(/\(百度搜索.*小说。\)/g,"")

ruleToc.chapterList:
+<js>
var chapters = [];
var regex = /<dd>[^<]*<a href="([^"]*)[^>]*>([^<]*)/g;
var match;
while ((match = regex.exec(result)) != null) {
    chapters.push({url: match[1], title: match[2]});
}
chapters.sort((x,y)=>x.url>y.url?1:-1);
</js>

